function Install-MDT2013 {    <#    .SYNOPSIS    Gets computer information from local or remote machine.    .DESCRIPTION    Stuff n WMI ports open plx.    .PARAMETER ComputerName    Name of computer to query. Accepts multiple values and accepts values from pipleline.    .PARAMETER IPAddress    The IP address to query. Accepts multiple values but not from pipeline.    .PARAMETER ShowProgress    Displays a progress bar showing status and percent complete.    .EXAMPLE    Get-NASystemInfo -ComputerName WHATEVER    This will query WHATEVER for system information.    .EXAMPLE    Get-NASystemInfo -ComputerName WHATEVER | Format-Table *    This will query WHATEVER and put the information in a table.    .EXAMPLE    Get-NASystemInfo -IPAddress x.x.x.x    Queries using IP instead of computer name.    #>[CmdletBinding()]param(    [Parameter(Mandatory=$True)]    [string]$Deploymentshare        #[Parameter()]    #[string[]]$Description)BEGIN{}PROCESS{    New-PSDrive -Name Root -PSProvider FileSystem -Root c:\4install\if(Test-Path Root:\Source\adksetup.exe){        Write-Host "adksetup.exe exists, moving on"         }        else{        Write-Host "Downloading adksetup.exe"        Invoke-WebRequest http://download.microsoft.com/download/6/A/E/6AEA92B0-A412-4622-983E-5B305D2EBE56/adk/adksetup.exe -OutFile Root:\Source\adksetup.exe        Write-Host "Finsihed downloading adksetup.exe"        }        if(Test-Path Root:\Source\MicrosoftDeploymentToolkit2013_x64.msi){        Write-Host "MicrosoftDeploymentToolkit2013_x64.msi exists, mocing on"        }        else{        Write-Host "Downloading MicrosoftDeploymentToolkit2013_x64.msi"        Invoke-WebRequest http://download.microsoft.com/download/B/F/5/BF5DF779-ED74-4BEC-A07E-9EB25694C6BB/MicrosoftDeploymentToolkit2013_x64.msi -OutFile Root:\Source\MicrosoftDeploymentToolkit2013_x64.msi        Write-Host "Finished downloading MicrosoftDeploymentToolkit2013_x64.msi"        }    Write-Verbose "Installing WDS features"    Install-WindowsFeature WDS-Deployment, WDS-AdminPack    Write-Verbose "Finished installing WDS features"    Write-Verbose "Starting ADK 8.1 installation"    Start-Process Root:\Source\adksetup.exe -ArgumentList "/features OptionId.DeploymentTools OptionId.WindowsPreinstallationEnvironment /ceip off /norestart /log c:\tempere\log.txt /quiet" -Wait    Write-Verbose "Finished installing ADK8.1"    Write-Verbose "Starting Microsoft Deployment Toolkit 2013 installation"    Start-Process Root:\Source\MicrosoftDeploymentToolkit2013_x64.msi /qn    Write-Verbose "Finished installing Microsoft Deployment Toolkit 2013"    Write-Verbose "Creating folder $Deploymentshare and sharing it"    New-Item -ItemType Directory -Path $Deploymentshare
    New-SmbShare -Name DeploymentShare$ -Path $DeploymentShare -FullAccess Administrator -ReadAccess Everyone}END{}}